import React, { Component } from 'react';
import CardAssignmentContainer from '../containers/CardAssignmentContainer';
import MoveCardToListContainer from '../containers/MoveCardToListContainer';

class Card extends Component {
  state = { showOptions: false };

  toggleOptions = () => {
    this.setState(prevState => {
      return { ...prevState, showOptions: !prevState.showOptions };
    });
  };

  handleChange = event => {
    const newListId = event.target.value;
    const { card } = this.props;

    //this.props.onListChange(card, newListId);
  };

  render() {
    const {
      card = {},
      lists = [],
      list = {},
      onRemoveCard = () => {},
    } = this.props;
    const { showOptions } = this.state;
    const removeCard = () => onRemoveCard(list, card);

    return (
      <article className="Card">
        <h3>{card.title}</h3>
        <CardAssignmentContainer key={card.id} cardId={card.id} />
        <div className="Card-description">{card.description}</div>
        {showOptions && (
          <div className="Card-options">
            <MoveCardToListContainer key={list.id} listId={list.id} />
            <button onClick={removeCard} className="Card-remove danger">
              Remove Card
            </button>
          </div>
        )}
        <button className="Card-toggle" onClick={this.toggleOptions}>
          Toggle Options
        </button>
      </article>
    );
  }
}

export default Card;
