import { connect } from 'react-redux';
import { denormalize, schema } from 'normalizr';
import CardAssignment from '../components/CardAssignment';

const mapStateToProps = (state, ownProps) => {
  const user = new schema.Entity('users');
  const mySchema = { users: [user] };
  console.log('in card assignment', state.users.ids);
  console.log('in card assignment', state.users.entities);
  const denormalizedUsers = denormalize({ users: state.users.ids }, mySchema, {
    users: state.users.entities,
  });
  console.log('in card assignment', denormalizedUsers);
  return {
    user:
      state.users.entities[state.cards.entities[ownProps.cardId].assignedTo],
    card: state.cards.entities[ownProps.cardId],
    users: denormalizedUsers,
  };
};

export default connect(mapStateToProps)(CardAssignment);
